import React, { useState } from 'react'

function Signup() {
    const [data, setData]=useState({})
    const eventHandle=(e)=>{
        setData({...data,[e.target.name]:e.target.value})
    }
    const clickHandler=()=>{
        fetch('http://2e8cc01927fc.ngrok.io/user/signup',{
            method:'POST',
            body:JSON.stringify(data),
        })
        .then((response)=>{
            response.json()})
            .then((result)=>{
                console.log(result)
                setData('')
            })
            .catch((error)=>{
                console.log(error)
            })
    }
    return (
      <div>
          <div>
        <input type='text' name='name' value={data.name} onChange={eventHandle} placeholder="name" ></input>
        </div>
        <div>
        <input type='text' name='email' value={data.email} onChange={eventHandle} placeholder="email" ></input>
        </div>
        <div>
        <input type='text' name='password' value={data.password} onChange={eventHandle} placeholder="password" ></input>
        </div>
        <button onClick={clickHandler}>ClickIt</button>
      </div>
    );
  }
  
  export default Signup;
