import axios from 'axios'
import React,{useState} from 'react'

function PostData2() {
    const [mydata,setMydata]=useState({})

    const handleChange=((event)=>{
        setMydata({...mydata,[event.target.name]:event.target.value})
    })

    const handleClick=()=>{
        const url='https://reqres.in/api/users'
        axios.post(url,mydata)
        .then((response)=>{
            console.log(response)
            
        })
    }
    return (
      <div>
          <button onClick={handleClick} >PostData</button>
          <input type='text' name='Name' value={mydata.movies} onChange={handleChange} ></input>
          <input type='text' name='movies' value={mydata.movies} onChange={handleChange} ></input>
          {/* <div> {mydata.name} </div>
          <div> {mydata.movies} </div> */}
      </div>
    );
  }
  
  export default PostData2;
