import axios from 'axios'
import React, { useState } from 'react'

function CurrencyConvert() {
    const [convert,setConvert]=useState({USD_PHP:''})

    const handleChange=(event)=>{
        setConvert(event.target.value)
    }
    const handleClick=()=>{
        const url='https://free.currconv.com/api/v7/convert?q=USD_PHP&compact=ultra&apiKey=7b0d039ba7b0583ccaba'
        axios.get(url)
        .then((response)=>{
            console.log(response.data.USD_PHP)
            
            const a=setConvert(response.data.USD_PHP)
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    return (
      <div>
          <input type='text' onChange={handleChange} placeholder='Enter currency' value={USD_PHP} ></input>
        <button onClick={handleClick} >InrToDoller</button>
      </div>
    );
  }
  
  export default CurrencyConvert;
