import axios from 'axios'
import React, { useState } from 'react'

function CurrencyConvert() {
    const [convert,setConvert]=useState({})

    const handleChange=(event)=>{
        setConvert(event.target.value)
    }
    const handleClick=()=>{
        const url='https://free.currconv.com/api/v7/convert?q=USD_PHP&compact=ultra&apiKey=7b0d039ba7b0583ccaba'
        axios.get(url)
        .then((response)=>{
            console.log(response.h)
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    return (
      <div>
          <input type='text' onChange={handleChange} placeholder='Enter currency' value={convert.name} name='INR' ></input>
        <button onClick={handleClick} >InrToDoller</button>
      </div>
    );
  }
  
  export default CurrencyConvert;
